var thumbnail_mode = "no-float" ;
summary_noimg = 600;
summary_img = 550;
img_thumb_height = 100;
img_thumb_width = 165;

//
function removeHtmlTag(strx,chop){
  if(strx.indexOf("<")!=-1) {
    var s = strx.split("<");
    for(var i=0;i<s.length;i++){
      if(s[i].indexOf(">")!=-1){
        s[i] = s[i].substring(s[i].indexOf(">")+1,s[i].length);
      }
    }
    strx = s.join("");
  }
  chop = (chop < strx.length-1) ? chop : strx.length-2;
  while(strx.charAt(chop-1)!=' ' && strx.indexOf(' ',chop)!=-1) chop++;
  strx = strx.substring(0,chop-1);
  return strx+'...';
}

function createSummaryAndThumb(pID){
  var div = $("#" + pID )[0];
  var imgtag = "";
  var img = $(div).find("img");

  if(img.length<=0) {
    imgtag = '<span style="float:left;margin-right:5px;"><img src="http://lh4.googleusercontent.com/-hqN1fgDzqdw/TSsOlsKSJ0I/AAAAAAAAAXU/eF3KNsS0wC4/s468/0e4e44e1.jpg" width="'+img_thumb_width+'px" height="'+img_thumb_height+'px"/></span>';
    var summ = summary_noimg;
  }

  if(img.length>=1) {
    var w = img[0].width;
    var h = img[0].height;
    var width,height;
    if(w > img_thumb_width) {
      width = img_thumb_width;
      height = h*(img_thumb_width/w);
    } else {
      width = w;
      height = h;
    }
    imgtag = '<span style="float:left;margin-right:15px;"><img src="'+img[0].src+'" width="'+ width +'px" height="'+ height +'px"/></span>';
    summ = summary_img;
  }

  var summary = imgtag + '<div>' + removeHtmlTag(div.innerHTML,summ) + '</div>';
  div.innerHTML = summary;
}
